import React from "react";

function Cards(props) {
  return (
    <div className="cards">
      <div className="card">
        <img className="card-img" src={props.imgSrc} alt="poster" />
        </div>
        <div className="card-info">
          <h3 className="ott-plateform">{props.ottPf}</h3>
          <h1 className="seriesName">{props.seriesName}</h1>
          <h3 className="imdb-rating">{props.imdb}</h3>
          <a href={props.srcLink} target="_blank" rel="noreferrer">
            <button>Watch Now</button>
          </a>
      </div>
    </div>
  );
}

export default Cards;
